CREATE TABLE pat_org_dept_info(id VARCHAR(64) PRIMARY KEY, org_id VARCHAR(64) DEFAULT NULL, parent_id VARCHAR(64) DEFAULT NULL, dept_name VARCHAR(30) DEFAULT NULL, code VARCHAR(64) DEFAULT NULL, short_name VARCHAR(30) DEFAULT NULL, short_code VARCHAR(20) DEFAULT NULL, dept_logo VARCHAR(150) DEFAULT NULL, deleted_flag INT DEFAULT 0, created_time DATETIME DEFAULT NULL, created_by VARCHAR(64) DEFAULT NULL, update_time DATETIME DEFAULT NULL, update_by VARCHAR(64) DEFAULT NULL)