CREATE TABLE pat_org_info(id VARCHAR(64) PRIMARY KEY, title VARCHAR(30) DEFAULT NULL, code VARCHAR(64) DEFAULT NULL, org_type_code VARCHAR(64) DEFAULT NULL, full_name VARCHAR(80) DEFAULT NULL, short_name VARCHAR(30) DEFAULT NULL, short_code VARCHAR(16) DEFAULT NULL, parent_id VARCHAR(64) DEFAULT NULL, org_logo_id VARCHAR(64) DEFAULT NULL, website VARCHAR(150) DEFAULT NULL, email VARCHAR(150) DEFAULT NULL, telephone VARCHAR(150) DEFAULT NULL, contact_person VARCHAR(30) DEFAULT NULL, summary VARCHAR(255) DEFAULT NULL, remark VARCHAR(255) DEFAULT NULL, status INT DEFAULT 1, deleted_flag INT DEFAULT 0, created_time DATETIME DEFAULT NULL, created_by VARCHAR(64) DEFAULT NULL, update_time DATETIME DEFAULT NULL, update_by VARCHAR(64) DEFAULT NULL)